import React, {
	useCallback,
	useRef,
	useState,
	useEffect
} from "react";
import "./DragDrop.css";

const Logo = () => (
	<svg className="icon" x="0px" y="0px" viewBox="0 0 24 24">
		<path fill="transparent" d="M0,0h24v24H0V0z" />
		<path
			fill="#000"
			d="M20.5,5.2l-1.4-1.7C18.9,3.2,18.5,3,18,3H6C5.5,3,5.1,3.2,4.8,3.5L3.5,5.2C3.2,5.6,3,6,3,6.5V19  c0,1.1,0.9,2,2,2h14c1.1,0,2-0.9,2-2V6.5C21,6,20.8,5.6,20.5,5.2z M12,17.5L6.5,12H10v-2h4v2h3.5L12,17.5z M5.1,5l0.8-1h12l0.9,1  H5.1z"
		/>
	</svg>
);

const handleFileUpload = (event) => {
	console.log("File uploaded:", event.target.files);
};

const UploadBox = () => {
	return (
		<label className="preview">
			<input type="file" onChange={handleFileUpload} className="upload" />
			<Logo />
			<p className="preview_msg">Drag and Drop File Here</p>
			<p className="preview_desc">XLSX</p>
		</label>
	);
};

const DragDrop = () => {
	const [isDragging, setIsDragging] = useState(false);
	const [files, setFiles] = useState([]);

	const dragRef = useRef(null);
	const fileId = useRef(0);

	const onChangeFiles = useCallback(
		(e) => {
			let selectFiles = [];
			let tempFiles = files;

			if (e.type === "drop") {
				selectFiles = e.dataTransfer.files;
			} else {
				selectFiles = e.target.files;
			}

			for (const file of selectFiles) {
				tempFiles = [
					...tempFiles,
					{
						id: fileId.current++,
						object: file
					}
				];
			}

			setFiles(tempFiles);
		},
		[files]
	);

	const handleFilterFile = useCallback(
		(id) => {
			setFiles(files.filter((file) => file.id !== id));
		},
		[files]
	);

	const handleDragIn = useCallback((e) => {
		e.preventDefault();
		e.stopPropagation();
	}, []);

	const handleDragOut = useCallback((e) => {
		e.preventDefault();
		e.stopPropagation();

		setIsDragging(false);
	}, []);

	const handleDragOver = useCallback((e) => {
		e.preventDefault();
		e.stopPropagation();

		if (e.dataTransfer.files) {
			setIsDragging(true);
		}
	}, []);

	const handleDrop = useCallback(
		(e) => {
			e.preventDefault();
			e.stopPropagation();

			onChangeFiles(e);
			setIsDragging(false);
		},
		[onChangeFiles]
	);

	const initDragEvents = useCallback(() => {
		if (dragRef.current !== null) {
			dragRef.current.addEventListener("dragenter", handleDragIn);
			dragRef.current.addEventListener("dragleave", handleDragOut);
			dragRef.current.addEventListener("dragover", handleDragOver);
			dragRef.current.addEventListener("drop", handleDrop);
		}
	}, [handleDragIn, handleDragOut, handleDragOver, handleDrop]);

	const resetDragEvents = useCallback(() => {
		if (dragRef.current !== null) {
			dragRef.current.removeEventListener("dragenter", handleDragIn);
			dragRef.current.removeEventListener("dragleave", handleDragOut);
			dragRef.current.removeEventListener("dragover", handleDragOver);
			dragRef.current.removeEventListener("drop", handleDrop);
		}
	}, [handleDragIn, handleDragOut, handleDragOver, handleDrop]);

	useEffect(() => {
		initDragEvents();

		return () => resetDragEvents();
	}, [initDragEvents, resetDragEvents]);

	return (
		<div className="DragDrop">
			<UploadBox />
			<div className="DragDrop-Files">
				{files.length > 0 &&
					files.map((file) => {
						const {
							id,
							object: { name }
						} = file;

						return (
							<div key={id}>
								<div>{name}</div>
								<div
									className="DragDrop-Files-Filter"
									onClick={() => handleFilterFile(id)}
								>
									X
								</div>
							</div>
						);
					})}
			</div>
		</div>
	);
};

export default DragDrop;
