import React, { useState } from 'react';

function DragAndDropUpload() {
	const [dragging, setDragging] = useState(false);
	const [files, setFiles] = useState([]);

	const handleDragEnter = (e) => {
		e.preventDefault();
		e.stopPropagation();
		setDragging(true);
	};

	const handleDragLeave = (e) => {
		e.preventDefault();
		e.stopPropagation();
		setDragging(false);
	};

	const handleDragOver = (e) => {
		e.preventDefault();
		e.stopPropagation();
	};

	const handleDrop = (e) => {
		e.preventDefault();
		e.stopPropagation();
		setDragging(false);
		let droppedFiles = e.dataTransfer.files;
		if (droppedFiles && droppedFiles.length > 0) {
			setFiles(prev => [...prev, ...droppedFiles]);
			e.dataTransfer.clearData();
		}
	};

	return (
		<div
			onDragEnter={handleDragEnter}
			onDragLeave={handleDragLeave}
			onDragOver={handleDragOver}
			onDrop={handleDrop}
			style={{
				border: '2px dashed gray',
				height: '200px',
				display: 'flex',
				alignItems: 'center',
				justifyContent: 'center',
				background: dragging ? '#e8e8e8' : 'white'
			}}
		>
			{dragging ? <p>Drop your files here</p> : <p>Drag and drop files here or click to select</p>}
			<input
				type="file"
				multiple
				style={{ display: 'none' }}
				onChange={(e) => setFiles(prev => [...prev, ...e.target.files])}
				ref={inputRef => inputRef && (inputRef.onclick = (e) => { e.stopPropagation(); })}
			/>
		</div>
	);
}

export default DragAndDropUpload;
