import React, { useState, useEffect, useRef } from "react";
import * as d3 from "d3";

const Histogram = ({ data }) => {
  const [distribution, setDistribution] = useState([]);
  const svgRef = useRef(null);

  const width = 500;
  const height = 300;
  const margin = { top: 20, right: 20, bottom: 30, left: 40 };

  const y = d3
    .scaleBand()
    .domain([1, 2, 3, 4, 5, 6, 7, 8, 9]) // Leading digits 1-9
    .rangeRound([margin.top, height - margin.bottom])
    .padding(0.1);

  const x = d3
    .scaleLinear()
    .domain([0, 0.7]) // Set maximum value to 70%
    .nice()
    .rangeRound([margin.left, width - margin.right]);

  const xTickFormat = d3.format(".0%"); // Format ticks as percentages

  function computeBenfordDistribution(data) {
    const totalCount = data.length;
    const distribution = Array(9).fill(0);

    data.forEach((value) => {
      const leadingDigit = parseInt(String(value)[0]);
      if (leadingDigit > 0) {
        distribution[leadingDigit - 1] += 1;
      }
    });

    return distribution.map((count) => count / totalCount);
  }

  function idealBenfordDistribution() {
    return [0.301, 0.176, 0.125, 0.097, 0.079, 0.067, 0.058, 0.051, 0.046];
  }

  useEffect(() => {
    if (data.length) {
      const benfordDistribution = computeBenfordDistribution(data);
      setDistribution(benfordDistribution);
    }
  }, [data]);

  useEffect(() => {
    if (distribution.length) {
      const svg = d3.select(svgRef.current);

      svg.selectAll("*").remove(); // Clear previous rendering

      svg.attr("width", width).attr("height", height);

      // Bars
      svg
        .selectAll("rect")
        .data(distribution)
        .enter()
        .append("rect")
        .attr("x", margin.left)
        .attr("y", (d, i) => y(i + 1))
        .attr("width", (d) => x(d) - margin.left)
        .attr("height", y.bandwidth())
        .attr("fill", "steelblue");

      // Add percentages to the right of each bar
      svg
        .selectAll(".percentage-label")
        .data(distribution)
        .enter()
        .append("text")
        .attr("class", "percentage-label")
        .attr("x", (d) => x(d) + 5) // Adjust position as needed
        .attr("y", (d, i) => y(i + 1) + y.bandwidth() / 2)
        .attr("dy", "0.35em")
        .style("text-anchor", "start")
        .text((d) => d3.format(".1%")(d));

      // Add dots to Benford's Law line
      svg
        .selectAll(".benford-dot")
        .data(idealBenfordDistribution())
        .enter()
        .append("circle")
        .attr("class", "benford-dot")
        .attr("cx", (d) => x(d))
        .attr("cy", (d, i) => y(i + 1) + y.bandwidth() / 2)
        .attr("r", 3)
        .attr("fill", "red");

      // Y-axis
      svg
        .append("g")
        .attr("transform", `translate(${margin.left}, 0)`)
        .call(d3.axisLeft(y).tickSizeOuter(0));

      // X-axis
      svg
        .append("g")
        .attr("transform", `translate(0, ${height - margin.bottom})`)
        .call(d3.axisBottom(x).ticks(7).tickFormat(xTickFormat)); // Adjust number of ticks
    } else {
      const svg = d3.select(svgRef.current);

      svg.selectAll("*").remove(); // Clear previous rendering

      svg.attr("width", width).attr("height", height);

      // Y-axis (empty)
      svg
        .append("g")
        .attr("transform", `translate(${margin.left}, 0)`)
        .call(d3.axisLeft(y).tickSizeOuter(0));

      // X-axis (empty)
      svg
        .append("g")
        .attr("transform", `translate(0, ${height - margin.bottom})`)
        .call(d3.axisBottom(x).ticks(7).tickFormat(xTickFormat)); // Adjust number of ticks

      // Add dots to empty graph
      svg
        .selectAll(".empty-dot")
        .data(idealBenfordDistribution())
        .enter()
        .append("circle")
        .attr("class", "empty-dot")
        .attr("cx", (d) => x(d))
        .attr("cy", (d, i) => y(i + 1) + y.bandwidth() / 2)
        .attr("r", 3)
        .attr("fill", "grey");
    }
  }, [distribution]);

  return <svg ref={svgRef}></svg>;
};

export default Histogram;
