import logo from "./logo.svg";
import "./App.css";
import "./DnD.css";
import React, { useState, useEffect, useRef } from "react";
import * as XLSX from "xlsx";
import Papa from "papaparse";
import Histogram from "./components/Histogram";


function App() {
  function readExcelData(file) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (event) => {
        const data = event.target.result;
        const workbook = XLSX.read(data, { type: "binary" });
        const firstSheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[firstSheetName];
        const csvData = XLSX.utils.sheet_to_csv(worksheet);

        Papa.parse(csvData, {
          complete: (results) => resolve(results.data),
          error: (error) => reject(error),
        });
      };
      reader.onerror = (error) => reject(error);
      reader.readAsBinaryString(file);
    });
  }
  const Logo = () => (
      <svg className="icon" x="0px" y="0px" viewBox="0 0 24 24">
        <path fill="transparent" d="M0,0h24v24H0V0z" />
        <path
            fill="#000"
            d="M20.5,5.2l-1.4-1.7C18.9,3.2,18.5,3,18,3H6C5.5,3,5.1,3.2,4.8,3.5L3.5,5.2C3.2,5.6,3,6,3,6.5V19  c0,1.1,0.9,2,2,2h14c1.1,0,2-0.9,2-2V6.5C21,6,20.8,5.6,20.5,5.2z M12,17.5L6.5,12H10v-2h4v2h3.5L12,17.5z M5.1,5l0.8-1h12l0.9,1  H5.1z"
        />
      </svg>
  );
  const UploadBox = () => {
    return (
        <label className="preview">
          <input type="file" onChange={handleFileUpload} style={{display: 'none'}} />
          <Logo />
          <p className="preview_msg">Drag and Drop File Here</p>
          <p className="preview_desc">XLSX</p>
        </label>
    );
  };
  const [data, setData] = useState([]);

  function extractNumericData(rawData) {
    // Filter rows that have numeric data.
    const numericRows = rawData.slice(4, 19); // Based on the given data, adjust if necessary

    let numbers = [];

    for (const row of numericRows) {
      // For each row, filter only the numeric values
      const numericValues = row.filter((cell) => !isNaN(cell) && cell !== "");
      numbers = numbers.concat(numericValues.map((val) => parseFloat(val)));
    }

    return numbers;
  }

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      const rawData = await readExcelData(file);
      const numericData = extractNumericData(rawData);
      setData(numericData);
    }
  };

  return (
    <div className="App">
      <UploadBox  />
      <Histogram data={data} />
    </div>
  );
}

export default App;
